import React, {useState} from "react";
const SearchBar = () =>{
    const [query,setQuerry]=useState("")
    function search(e){
        e.preventDefault()
        setQuerry(e.target.value)
    }
return (
    <div>
        <input
        text="text"
        onChange={search}
        value={query}
        />
        <label>SearchBar</label>
        <button></button>
    </div>
);
};


export default SearchBar
